from collections import Counter


class Solution(object):
	def longestPalindrome(self, s):
		"""
		:type s: str
		:rtype: int
		"""
		c = Counter(s)
		length = 0
		flag = 0
		for value in c.values():
			if value % 2:
				length += value - 1
				flag = 1
			else:
				length += value
		return length + flag


print(Solution().longestPalindrome(
	""))
