#!/usr/local/bin/ python3
# -*- coding:utf-8 -*-
# __author__ = "zenmeder"
class Solution(object):
	def reverseStr(self, s, k):
		"""
        :type s: str
        :type k: int
        :rtype: str
        """
		if k <= 1:
			return s
		p = 0
		length = len(s)
		result = ''
		while True:
			q = 2 * k + p
			r = k + p
			if p == length:
				break
			result += s[p:r][::-1]
			if r >= length:
				break
			elif r<length and q>=length:
				result += s[r:q]
				break
			elif r<length and q< length:
				result += s[r:q]
				p = q
				continue
		return result
solution = Solution()
print(solution.reverseStr("",
127))
